import 'package:flutter/material.dart';

class StarRating extends StatelessWidget {
  final int rating;

  StarRating(this.rating);

  @override
  Widget build(BuildContext context) {
    return Row(
      children: List.generate(
        5,
        (index) => Icon(
          index < rating ? Icons.star : Icons.star_border,
          color: Colors.yellow,
        ),
      ),
    );
  }
}

class OthersTabPage extends StatefulWidget {
  const OthersTabPage({super.key});

  @override
  State<OthersTabPage> createState() => _OthersTabPageState();
}

class _OthersTabPageState extends State<OthersTabPage> {
  String promoCode = '';
  int _currentPage = 0;

  List<String> _imageUrls = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTtZDzqJs5dcPE0tYnWRzxdJhVfmxeH8yVcow&usqp=CAU',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRVv-xc8sx5v4-2wGCiunth82WNWhYhyEBlFA&usqp=CAU',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRk33zJmGjLnf011UHn99C4mmtw743494l4Sw&usqp=CAU'
  ];

  void _applyPromo() {
    // Aksi saat tombol ditekan
    // Misalnya, menampilkan pesan atau melakukan tindakan lain
    setState(() {
      promoCode = 'PROMO20'; // Ganti dengan logika sesuai kebutuhan
    });
  }

  @override
  Widget build(BuildContext context) {
    return Align(
      alignment: Alignment.topLeft,
      child: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text(
              'Promo Code: $promoCode',
              style: TextStyle(fontSize: 18.0),
            ),
            SizedBox(height: 20.0),
            ElevatedButton(
              onPressed: _applyPromo,
              child: Text('Apply Promo'),
            ),
            SizedBox(height: 20.0),
            Container(
              height: 450.0,
              width: 450.0,
              child: PageView.builder(
                onPageChanged: (index) {
                  setState(() {
                    _currentPage = index;
                  });
                },
                itemCount: _imageUrls.length,
                itemBuilder: (context, index) {
                  return Column(
                    children: [
                      Container(
                        height: 300.0,
                        width: 300,
                        child: Image.network(
                          _imageUrls[index],
                          fit: BoxFit.cover,
                        ),
                      ),
                      SizedBox(height: 10.0),
                      StarRating(3),
                      Text('Rate Us!!'),
                    ],
                  );
                },
              ),
            ),
            SizedBox(height: 10.0),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: _buildPageIndicator(),
            ),
          ],
        ),
      ),
    );
  }

  List<Widget> _buildPageIndicator() {
    List<Widget> indicators = [];
    for (int i = 0; i < _imageUrls.length; i++) {
      indicators.add(
        i == _currentPage ? _indicator(true) : _indicator(false),
      );
    }
    return indicators;
  }

  Widget _indicator(bool isActive) {
    return Container(
      height: 8.0,
      width: 8.0,
      margin: EdgeInsets.symmetric(horizontal: 4.0),
      decoration: BoxDecoration(
        shape: BoxShape.circle,
        color: isActive ? Colors.blue : Colors.grey,
      ),
    );
  }
}
